function CartReducer(state={
  cart: [],
  success: false
}, action) {
 //alert(action.payload?.data.data);

  switch (action.type) {
      case "ADDTOCART" : {
          state = {...state}
          state.data = action.payload?.data
          return state
      }
      case "SHOW_CART" : {
          state = {...state}
          state["cart"] = action.payload?.data
          state["cartcount"] =  state["cart"].length
          state["success"] = false
          return state
      }
      case "EMPTYCART" : {
          state = {...state}
          state.cart = []
          return state
      }
      case "REMOVEFROMCART" : {
          state = {...state}
          return state
      }
      default : return state
  }
}

export default CartReducer
