import React, { useState,useEffect } from 'react';
import ReactDOM from 'react-dom';
import Footer from './Footer';
import axios from 'axios';
import './cart.css'
import {withRouter} from "react-router-dom"
import { connect } from "react-redux"


function Cart(props) {

    var [cakess,setCakess] = useState([]);


const apiUrl1 = "https://apibyashu.herokuapp.com/api/cakecart";  
const datas = {};
const getAllCakes = () => { 
  axios.post(apiUrl1, datas, {
    headers: {
      'authtoken': localStorage.token
    },
  })
  .then((res) => {
   console.log(res.data.data)
   props.dispatch({
       type:"SHOW_CART",
       payload:{
        data: setCakess(res.data)
       }
   })

  })
  .catch((error) => {
    console.error(error)
  })
}; 

 useEffect(() => {
   getAllCakes();

 }, []);



    return (
        <div className="container-fluid mt-5">
        <h2 className="mb-5 text-center">Shopping Cart</h2>
        <div className="row justify-content-center">
            <div className="col-md-8">
                <div className="table-responsive">
                    <table id="myTable" className="table">
                        <thead>
                            <tr>
                                <th>Product</th>
                                <th>Name</th>
                                <th>Qty</th>
                                <th>Price</th>
                                <th className="text-right"><span id="amount" className="amount">Amount</span> </th>
                            </tr>
                        </thead>
                        <tbody>
                            <tr>
                                <td>
                                    <div className="product-img">
                                        <div className="img-prdct"><img src="https://www.pos-agent.com/app_v2/assets/upload/product/product_892_20180823125535.jpg" /></div>
                                    </div>
                                </td>
                                <td>
                                    <p>Product One</p>
                                </td>
                                <td>
                                    1
                                </td>
                                <td>
                                    <input type="text" value="72" className="price form-control" disabled />
                                </td>
                                <td >$ <span id="amount" className="amount">0</span></td>
                            </tr>

                        </tbody>
                        <tfoot>
                            <tr>
                                <td colspan="4"></td><td><strong>TOTAL = $  <span id="total" className="total">0</span></strong></td>
                            </tr>
                        </tfoot>
                    </table>
                </div>
            </div>
        </div>
    </div>
    )
}

Cart = withRouter(Cart)

export default connect()(Cart);
