import React, { useState,useEffect } from 'react';
import ReactDOM from 'react-dom';
import Footer from './Footer';
import { useHistory } from "react-router-dom";
import axios from 'axios';
import {connect} from "react-redux"
import { loginmiddleware } from '../reduxstore/middlewares';

function Login(props){

    const [employee, setemployee] = useState({ email: '', password: ''}); 
    const [loginmes, setloginmes] = useState('');  
    const apiUrl1 = "https://apibyashu.herokuapp.com/api/login";  

    let history = useHistory();

    const Login = (e) => {    
    e.preventDefault();    

    const datas = { email:employee.email, password: employee.password };    
       var middlefunction = loginmiddleware(this.state)
			 props.dispatch(middlefunction)

  };    


  const onChange1 = (e) => {    
    e.persist();    

    setemployee({...employee, [e.target.name]: e.target.value});    
  }  

    return(
        <div>
        <div className="container-fluid" style={{ marginTop:"50px"}}>

     <h2> Login Form</h2>

     {loginmes=='Invalid Credentials' ?(
           <div className="alert alert-danger" role="alert">
           {loginmes}
         </div>
        )
      :("")
      }

        <form onSubmit={Login}>

          <div className="form-group">
            <label for="email">Email:</label>
            <input type="email" className="form-control" id="email" placeholder="Enter Email" name="email" value={employee.email} onChange={onChange1} />
            <div style={{ fontSize: 12, color: "red" }}>  </div>
          </div>
          <div className="form-group">
            <label for="pwd">Password:</label>
            <input type="password" className="form-control" id="password" placeholder="Enter Password" name="password" value={employee.password} onChange={onChange1} />
            <div style={{ fontSize: 12, color: "red" }}>  </div>
          </div>
          <button type="submit" className="btn btn-success">Submit</button>
        </form>

     </div>
     <Footer></Footer>
      </div>

    )
}

export default connect()(Login);
