import React,{useEffect, useState} from "react";
import {Link, withRouter} from "react-router-dom";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {connect} from "react-redux";
import StarRatings from 'react-star-ratings';
import ReactPaginate from "react-paginate";
import axios from "axios";

let Cake = (props) => {
    const [offset, setOffset] = useState(0);
    const [data, setData] = useState([]);
    const [perPage] = useState(10);
    const [pageCount, setPageCount] = useState(0)

const getData = async() => {
    const res = await axios.get(process.env.REACT_APP_API_BASE_URL +'/allcakes')
    const data = res.data;
              const slice = data.slice(offset, offset + perPage)
              const postData = slice.map(pd => <div key={pd.id}>
                   <div className="card" style={{width: '28rem',marginLeft:'10px',height:'30em'}}>
         <Link to={'/cake/'+pd.cakeid}>
        <img src={pd.image} className="card-img-top" alt={pd.image} style={{height: '20rem'}} /> </Link>
        <div className="card-body">
          <h5 className="card-title">{pd.name}</h5>
          <p className="card-text"> Rs.{pd.price}</p>
          <p>
            <StarRatings rating={pd.ratings} starRatedColor="yellow" numberOfStars={5} starDimension="25px" starSpacing="1px" name='rating'/>
          </p>

        </div>

      </div>
              </div>)
              setData(postData)
              setPageCount(Math.ceil(data.length / perPage))
}
const handlePageClick = (e) => {
  const selectedPage = e.selected;
  setOffset(selectedPage + 1)
};

useEffect(() => {
 getData()
}, [offset])
        return (
          <div>
         {data}

         <ReactPaginate
                    previousLabel={"prev"}
                    nextLabel={"next"}
                    breakLabel={"..."}
                    breakClassName={"break-me"}
                    pageCount={pageCount}
                    marginPagesDisplayed={2}
                    pageRangeDisplayed={5}
                    onPageChange={handlePageClick}
                    containerClassName={"pagination"}
                    subContainerClassName={"pages pagination"}
                    activeClassName={"active"}/>


      </div>

        );

      }

      export default Cake;
