import React, { useState,useEffect } from 'react';
import { Link} from 'react-router-dom';
import ReactDOM from 'react-dom';
import axios from 'axios';
import Footer from './Footer';
import './details.css';


function CakeDetails(props) {

 var [cakess,setCakess] = useState([]);

 const getAllCakes = () => {
  axios.get('https://apibyashu.herokuapp.com/api/cake/'+props.match.params.cakeid).then((res) => {
       console.log("",res.data.data)
       const gettingcak = res.data.data;
       setCakess(gettingcak);



   })
}
useEffect(() => {
  getAllCakes();

}, []);

  return (
    <div>
    <main className="container" >


<div className="left-column" style={{marginRight:"30px"}}>

  <img data-image="red" className="active" src={cakess.image} style={{height:"500px"}} />
</div>


<div className="right-column">


  <div className="product-description">
    <span>{cakess.cakeid}</span>
    <h1>Beats EP</h1>
    <p>The preferred choice of a vast range of acclaimed DJs. Punchy, bass-focused sound and high isolation. Sturdy headband and on-ear cushions suitable for live performance</p>
  </div>


  <div className="product-configuration">


    <div className="product-color">

    </div>


    <div className="cable-config">
      <span>Cable configuration</span>

      <div className="cable-choose">
        <button>Straight</button>
        <button>Coiled</button>
        <button>Long-coiled</button>
      </div>

      <a href="#">How to configurate your headphones</a>
    </div>
  </div>


  <div className="product-price">
    <span>148$</span>
    <a href="#" class="cart-btn">Add to cart</a>
  </div>
</div>
</main>

    </div>
  );
}

export default CakeDetails;
