import React, { useState,useEffect } from 'react';
import { Link} from 'react-router-dom';
import ReactDOM from 'react-dom';
import querystring from 'query-string'
import axios from 'axios';
import Footer from './Footer';

function Search(props) {
 var query = querystring.parse(props.location.search)
 var [cakess,setCakess] = useState([]);
 var [isloading,setloading] = useState(true); 
 const getAllCakes = () => {
  axios.get('https://apibyashu.herokuapp.com/api/searchcakes?q='+query.q).then((res) => {
       console.log("",res.data)
       const gettingcak = res.data.data;
       setCakess(gettingcak);
       setloading(false)


   })
}
useEffect(() => {
  getAllCakes();

},[query]);


  return (

    <div>   
     <div className="row" style={{marginTop:'50px'}}>  {
     cakess.map((cookies,index)=>{
        return(  

     <div className="card" style={{width: '28rem',marginLeft:'10px',height:'30em'}} key={index}>

  <Link to={'/cake/'+cookies.cakeid}> <img src={cookies.image} className="card-img-top" alt={cookies.image} style={{height: '20rem'}} /> </Link>
  <div className="card-body">
    <h5 className="card-title">{cookies.name}</h5>
    <p className="card-text"> Rs.{cookies.price}</p>
    <a href="javascript.void(0);" className="btn btn-primary">Add To Cart</a>
  </div>
</div> 
           ) } ) } 

</div>
  {isloading && 
    <div> 
    <img src="https://i.gifer.com/YCZH.gif" style={{display:'block',marginLeft:'auto',marginRight:'auto',width:'50%'}} /> 
    </div> }
<Footer></Footer>
</div>     

  );
}

export default Search;
