import React, { useState,useEffect } from 'react';
import ReactDOM from 'react-dom';
import Footer from './Footer';
import axios from 'axios';

function Signup(props){

    const [data, setdata] = useState({ name: '', email: '', password: '' }) ;
    const [employee, setemployee] = useState({ email: '', password: ''}); 
    const [datames, setdatames] = useState('');
    const [loginmes, setloginmes] = useState('');  

  const apiUrl = "https://apibyashu.herokuapp.com/api/register";
  const apiUrl1 = "https://apibyashu.herokuapp.com/api/login";  
  const Registration = (e) => {  
    e.preventDefault();  

    const data1 = { name: data.name,email: data.email, password: data.password };  
    axios.post(apiUrl, data1)  
      .then((result) => {  
        var eraseVal = document.getElementById('formsVal').reset();
        console.log(result.data);  
        const mess = setdatames(result.data.message);

      })  
  }  
  const onChange = (e) => {  
    e.persist();  

    setdata({ ...data, [e.target.name]: e.target.value });  
  } 


  const Login = (e) => {    
    e.preventDefault();    

    const datas = { email:employee.email, password: employee.password };    
    axios.post(apiUrl1, datas)    
    .then((result) => {    
        console.log(result.data)
        const login_mess = setloginmes(result.data.message);
        const serializedState = JSON.stringify(result.data.token);  
        var a= localStorage.setItem('myData', serializedState);   


    })        
  };    


  const onChange1 = (e) => {    
    e.persist();    

    setemployee({...employee, [e.target.name]: e.target.value});    
  }  

    return(
        <div>
        <div className="container" style={{ marginTop:"50px"}}>
        <div className="row align-items-start">
        <div className="col-6">
        <h2> Registeration Form</h2>

        {datames=='User Already Exists' ?(
           <div className="alert alert-danger" role="alert">
           {datames}
         </div>
        )
        : datames=='User Registered' ?(
          <div className="alert alert-success" role="alert">
           {datames}
         </div>
        )
      :("")
      }        

        <form onSubmit={Registration} id="formsVal" >
        <div className="form-group">
            <label for="name"> Full Name:</label>
            <input type="name" className="form-control" id="name" placeholder="Enter Full Name" name="name" onChange={onChange} value={data.name}  />
            <div style={{ fontSize: 12, color: "red" }}>  </div>
          </div>
          <div className="form-group">
            <label for="email">Email:</label>
            <input type="email" className="form-control" id="email" placeholder="Enter Email" name="email" onChange={onChange} value={data.email}  />
            <div style={{ fontSize: 12, color: "red" }}>  </div>
          </div>
          <div className="form-group">
            <label for="pwd">Password:</label>
            <input type="password" className="form-control" id="password" placeholder="Enter Password" name="password" onChange={onChange} value={data.password}  />
            <div style={{ fontSize: 12, color: "red" }}>  </div>
          </div>
          <button type="submit" className="btn btn-success">Submit</button>
        </form>
    </div>

     <div className="col-6">

     <h2> Login Form</h2>

     {loginmes=='Invalid Credentials' ?(
           <div className="alert alert-danger" role="alert">
           {loginmes}
         </div>
        )
      :("")
      }

        <form onSubmit={Login}>

          <div className="form-group">
            <label for="email">Email:</label>
            <input type="email" className="form-control" id="email" placeholder="Enter Email" name="email" value={employee.email} onChange={onChange1} />
            <div style={{ fontSize: 12, color: "red" }}>  </div>
          </div>
          <div className="form-group">
            <label for="pwd">Password:</label>
            <input type="password" className="form-control" id="password" placeholder="Enter Password" name="password" value={employee.password} onChange={onChange1} />
            <div style={{ fontSize: 12, color: "red" }}>  </div>
          </div>
          <button type="submit" className="btn btn-success">Submit</button>
        </form>

     </div>
      </div>
      </div>
      </div> 

    )
}

export default Signup;
