import React from 'react';
import ReactDOM from 'react-dom';


class Form extends React.Component{
    constructor(props){
      super(props);
      this.state = {
        username: null,
        email: null,
        phone: null,
        errors: {
          username: '',
          email: '',
          phone: '',
        }
      };

    }
    handleChange = (event) => {
        event.preventDefault();
        const { name, value } = event.target;
        let errors = this.state.errors;
    }
render() {
  return (
      <div>
  <div className="container" style={{marginTop:'50px'}} >
  <form onSubmit={this.handleSubmit} noValidate>
  <div className="form-group">
      <label for="username">Name:</label>
      <input type="text" className="form-control" id="username" placeholder="Enter Name" name="username" onChange={this.handleChange} noValidate  />
      {errors.username.length > 0 && 
                <span className='error'>{errors.username}</span>}
    </div>
    <div className="form-group">
      <label for="email">Email:</label>
      <input type="email" className="form-control" id="email" placeholder="Enter Email" name="email" onChange={this.handleChange} noValidate  />
    </div>
    <div className="form-group">
      <label for="phone">Phone:</label>
      <input type="tel" className="form-control" id="phone" placeholder="Enter Phone" name="phone"  onChange={this.handleChange} noValidate />
    </div>

    <button type="submit" className="btn btn-default">Submit</button>
  </form>
</div>
</div>
  );
}
}

export default Form;
