import React, { useState,useEffect } from 'react';
import ReactDOM from 'react-dom';
import Footer from './Footer';
import axios from 'axios';
import './cart.css'
import {useHistory, withRouter} from "react-router-dom"
import { connect } from "react-redux"
import AddBoxOutlinedIcon from '@material-ui/icons/AddBoxOutlined';  
import IndeterminateCheckBoxOutlinedIcon from '@material-ui/icons/IndeterminateCheckBoxOutlined';

function Cart(props) {

    var [cakess,setCakess] = useState([]);
    const history = useHistory();
    var total = 0;
const apiUrl1 = "https://apifromashu.herokuapp.com/api/cakecart";  
const datas = {};
const getAllCakes = () => { 
  axios.post(apiUrl1, datas,{
    headers: {
      'authtoken': localStorage.token
    },
  })
  .then((res) => {
    setCakess(res.data.data)
    console.log(res.data.data)
    props.dispatch({
       type:"SHOW_CART",
       payload:{
        data: res.data
       }
   })

  })
  .catch((error) => {
    console.error(error)
  })
}; 

 useEffect(() => {
   getAllCakes();

 }, []);


 const  AddCake=(e,cakess)=>{
    let  apiurl =process.env.REACT_APP_BASE_URI+"addcaketocart";

    axios({
    method:"post",
    url:apiurl,
    headers:{
       authtoken:props.token
    },
    data:{
       cakeid:cakess.cakeid,
       name:cakess.name,
       image:cakess.image,
       price:cakess.price,
       weight:cakess.weight
    }}).then((res)=>{

        console.log(res.data);

  } ,(error)=>{
         // console.log(error)
  })
};


 let removeItem = (cakeid) => {
    let apiUrl = "https://apifromashu.herokuapp.com/api/removecakefromcart";
    axios({
        url: apiUrl,
        method: "POST",
        headers: { authtoken: localStorage.token },
        data: { cakeid: cakeid }
    }).then((response) => {

       console.log(response.data)
       history.push("/Cart")
    })
}



    return (
        <div className="container-fluid mt-5">
        <h2 className="mb-5 text-center">Shopping Cart</h2>
        <div className="row justify-content-center">
            <div className="col-md-8">
                <div className="table-responsive">
                    <table id="myTable" className="table">
                        <thead>
                            <tr>
                                <th>Product</th>
                                <th>Name</th>
                                <th>Qty</th>
                                <th>Price</th>
                                <th className="text-right"><span id="amount" className="amount">Amount</span> </th>
                                <th>Action</th>
                            </tr>
                        </thead>
                        <tbody>



                         {
                        cakess.map((data,index)=>{

                            total = total+ data.price * data.quantity;
                                return (
                            <tr key={index}>

                                <td>
                                    <div className="product-img">
                                        <div className="img-prdct"><img src={data.image}  /></div>
                                    </div>
                                </td>
                                <td>
                                    <p>{data.name}</p>
                                </td>
                                <td className="text-center">
                                            <AddBoxOutlinedIcon style={{cursor:'pointer'}}  onClick={(e)=>AddCake(e,data)}/>
                                            <div className="count-input">
                                                <label> {data.quantity} </label>
                                            </div>
                                            <IndeterminateCheckBoxOutlinedIcon style={{cursor:'pointer'}}  />
                                        </td>
                                <td>
                                    <input type="text" value={data.price} className="price form-control" disabled />
                                </td>
                                <td >Rs. <span id="amount" className="amount">{data.price * data.quantity}</span></td>
                                <td> <button className="btn btn-danger" onClick={()=>removeItem(data.cakeid)}><i className="fa fa-trash"></i></button>  </td>
                            </tr>
                             ) })}
                        </tbody>
                        <tfoot>
                            <tr>
                                <td colSpan="4"></td><td><strong>TOTAL = Rs.<span id="total" className="total">{total}</span></strong></td>
                            </tr>
                        </tfoot>
                    </table>
                </div>
            </div>
        </div>
    </div>
    )
}

Cart =  connect((state,props)=>{
 //alert(JSON.stringify(state.CartReducer?.cart.length))
     return {
       cart: state.CartReducer?.cart
     }
   })(Cart) 

   export default withRouter(Cart)
