import Cake from './Cake'
import axios from "axios";
import {useEffect, useState} from "react";
import {connect} from "react-redux";
import {withRouter} from "react-router-dom";
import Footer from './Footer';

const Cakelist = (props) => {
    const [cakes, getCakes] = useState([]);
    const [isLoading, setLoading] = useState(true)

    useEffect(() => {
        axios({
            url: process.env.REACT_APP_API_BASE_URL +'/allcakes',
            method: 'get'
        }).then(res => {
            const cakeList = res.data.data
            getCakes(cakeList);
            setLoading(false)
        }, err => {
            setLoading(false)
        })
    }, [])

    useEffect(() => {
        if (localStorage.getItem('token')) {
            axios({
                url: process.env.REACT_APP_API_BASE_URL + '/cakecart',
                method: 'post',
            }).then(res => {
                const cakeList = res.data.data
                props.dispatch({
                    type: "SHOW_CART",
                    payload: {
                        data: cakeList
                    }
                })
            }, err => {
            })
        }
    }, [])

    const getSortHigh = () =>{
        let sortedData = cakes.sort((a, b) => b.price - a.price)
      //  getCakes(sortedData)
        console.log(sortedData)
    }

    const getSortLow = () =>{
        let sortedDatas= cakes.sort((a, b) => a.price - b.price)
        getCakes(sortedDatas)
        console.log(sortedDatas)
    }

    return (
        <div>
            <button className="btn btn-primary" onClick={getSortHigh}>High to Low</button>
            <button className="btn btn-primary" onClick={getSortLow}>Low to High</button>

        <div className="row col-md-12">

            {
                cakes.map((each, index) => {
                    return (
                        <Cake data={each} key={index} page="list"/>
                    )
                })
            }
                { isLoading && <div> 
                    <img src="https://i.gifer.com/YCZH.gif" /> 
            </div>
            }

            <Footer></Footer>
        </div>
        </div>
    )
}

export default connect() (withRouter(Cakelist))
