function CartReducer(state={
  cart: [],
  totalItems: 0,
  success: false
}, action) {
  console.log(action.payload?.data);

  switch (action.type) {
      case "ADDTOCART" : {
          state = {...state}
          state['cartitem'] = action.payload?.data
          state["totalItems"] += state.cart.length
          state["success"] = true
          return state
      }
      case "SHOW_CART" : {
          state = {...state}
          state["totalItems"] = action.payload?.data
          state["success"] = false
          return state
      }
      case "EMPTYCART" : {
          state = {...state}
          state.cart = []
          return state
      }
      case "REMOVEFROMCART" : {
          state = {...state}
          return state
      }
      default : return state
  }
}

export default CartReducer
