import {Component} from "react"
import axios from 'axios'
import {withRouter} from "react-router-dom"
import { connect } from "react-redux"
import {loginmiddleware} from "../reduxstore/middlewares"
import Footer from "./Footer"

class Login extends Component{
	emailError
	passwordError
	apiUrl
	constructor(props){
		super(props)
		console.log(">>>>>. login props" , props)
		this.emailError=this.passwordError="";
		this.apiUrl="https://apibyashu.herokuapp.com/api/login"
		this.state={
			email:"",
			password:"",
		}
	}
	changeEmail=(event)=>{
		this.setState({
			email:event.target.value
		})
	}

	changePassword=(event)=>{
		this.setState({
			password:event.target.value
		})
	}

	validateEmail=(event)=>{
		this.emailError=this.passwordError="";
		event.preventDefault()
		var isValid=true;
		var inputEmail=this.state.email
		var inputPassword=this.state.password
		var pattern = new RegExp(/^(("[\w-\s]+")|([\w-]+(?:\.[\w-]+)*)|("[\w-\s]+")([\w-]+(?:\.[\w-]+)*))(@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$)|(@\[?((25[0-5]\.|2[0-4][0-9]\.|1[0-9]{2}\.|[0-9]{1,2}\.))((25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\.){2}(25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\]?$)/i);

		if(!inputEmail){
			isValid=false;
			this.emailError="Email is required Field"
		}else if(!pattern.test(inputEmail)){
			isValid=false;
			this.emailError="Invalid Email Syntax"
		}
		if(!inputPassword){
			isValid=false;
			this.passwordError="Password is required Field"
		}
		this.setState({
			emailError:this.emailError,
			passwordError:this.passwordError,
		})
		if(isValid){
			var middlefunction = loginmiddleware(this.state)
			 this.props.dispatch(middlefunction)
		}
	}

	render(){

    return(
        <div>
        <div className="container-fluid" style={{ marginTop:"50px"}}>

     <h2> Login Form</h2>



        <form onSubmit={this.validateEmail}>

          <div className="form-group">
            <label>Email:</label>
            <input type="email" className="form-control" id="email" placeholder="Enter Email" name="email" value={this.state.email} onChange={this.changeEmail} />
            {this.emailError && <small id="emailError" className="form-text form-error alert alert-danger">{this.emailError}</small>}
          </div>
          <div className="form-group">
            <label>Password:</label>
            <input type="password" className="form-control" id="password" placeholder="Enter Password" name="password" value={this.state.password} onChange={this.changePassword} />
            {this.passwordError && <small id="passwordError" className="form-text form-error alert alert-danger">{this.passwordError}</small>}
          </div>
          <button type="submit" className="btn btn-success">Submit</button>
          {this.props.isloading && <img src="https://i.gifer.com/YCZH.gif" style={{display:'block',marginLeft:'auto',marginRight:'auto',width:'50%'}} />  }
        </form>

     </div>
     < Footer />
      </div>

        )
      }

    }



     Login =connect(function(state,props){
    //  alert("props" + JSON.stringify(props))
      if(state.AuthReducer?.isloggedin==true){
          props.history.push("/")
      }else{
        return {
          isloading:state.AuthReducer?.isloading
        }
      }
    })(Login) 
    export default withRouter(Login)
