import React from 'react';
import ReactDOM from 'react-dom';
import Footer from './Footer';

class Form extends React.Component{

    constructor() {
        super();
        this.state = {

          name: "",
          email: "",
          mobile:"",
          password: "",
          nameError: "",
          emailError: "",
          mobileError:"",
          passwordError: ""

        }


      };

      handleChange = event => {

        this.setState({
          [event.target.name]: event.target.value
        });
      };

      validate = () => {
        let nameError = "";
        let emailError = "";
        let passwordError = "";
        let mobileError = "";

        let phonelength = this.state.mobile.length;

        if (!this.state.name) {
          nameError = "Name can not be blank";
        }
        if (!this.state.email.includes("@")) {
          emailError = "Invalid Email";
        }
        if (!this.state.email) {
          emailError = "Email can not be blank";
        }
        if (!this.state.mobile) {
          mobileError = "Mobile Number can not be blank";
        }
        if (phonelength <= 9) {
          mobileError = "Mobile Number should be 10 Digit";
        }
        if (!this.state.password) {
          passwordError = "Password can not be blank";
        }
        if ((this.state.password).length <= 4) {
          passwordError = "Password should not be less than 5 Digit";
        }

        if (emailError || nameError || mobileError || passwordError ) {
          this.setState({ emailError, nameError, passwordError, mobileError });
          return false;
        }

        return true;
      };

      handleSubmit = event => {
        event.preventDefault();
        const isValid = this.validate();
        if (isValid) {
         console.log(this.state)

        }
      };



    render() {
      return (
        <div>
        <div class="container" style={{ marginTop:"50px"}}>
        <h2> Registeration Form</h2>
        <form onSubmit={this.handleSubmit}>
        <div class="form-group">
            <label for="name"> Full Name:</label>
            <input type="name" class="form-control" id="name" placeholder="Enter Full Name" name="name" value={this.state.name} onChange={this.handleChange} />
            <div style={{ fontSize: 12, color: "red" }}> {this.state.nameError} </div>
          </div>
          <div class="form-group">
            <label for="email">Email:</label>
            <input type="text" class="form-control" id="email" placeholder="Enter Email" name="email" value={this.state.email} onChange={this.handleChange} />
            <div style={{ fontSize: 12, color: "red" }}> {this.state.emailError} </div>
          </div>
          <div class="form-group">
            <label for="mobile">Mobile Number:</label>
            <input type="tel" class="form-control" id="mobile" placeholder="Enter Mobile Number" name="mobile" value={this.state.mobile} onChange={this.handleChange} />
            <div style={{ fontSize: 12, color: "red" }}> {this.state.mobileError} </div>
          </div>
          <div class="form-group">
            <label for="pwd">Password:</label>
            <input type="password" class="form-control" id="password" placeholder="Enter Password" name="password" value={this.state.password} onChange={this.handleChange} />
            <div style={{ fontSize: 12, color: "red" }}> {this.state.passwordError} </div>
          </div>
          <button type="submit" class="btn btn-default">Submit</button>
        </form>
    </div>
      <Footer></Footer>

      </div>



        );
    }


  }


export default Form;
