import React, { useState,useEffect } from 'react';
import { Link} from 'react-router-dom';
import ReactDOM from 'react-dom';
import axios from 'axios';
import Footer from './Footer';
import './details.css';


function CakeDetails(props) {

 var [cakess,setCakess] = useState([]);
 var [cart,setCart] = useState([]);

 const getAllCakes = () => {
  axios.get('https://apibyashu.herokuapp.com/api/cake/'+props.match.params.cakeid).then((res) => {
       console.log("",res.data.data)
       const gettingcak = res.data.data;
       setCakess(gettingcak);



   })
}
useEffect(() => {
  getAllCakes();

}, []);

var cakessName = cakess.name;

const apiUrl1 = "https://apibyashu.herokuapp.com/api/addcaketocart";  
const datas = { cakeid:props.match.params.cakeid, name: cakess.name ,image:cakess.image,price:cakess.price,weight:cakess.weight};
const addTocart = () => { 
  axios.post(apiUrl1, datas, {
    headers: {
      'authtoken': localStorage.token
    },
  })
  .then((res) => {
    console.log(res.data)
  })
  .catch((error) => {
    console.error(error)
  })
}; 

  return (
    <div>
    <main className="container" >


<div className="left-column" style={{marginRight:"30px"}}>

  <img data-image="red" className="active" src={cakess.image} style={{height:"500px"}} />
</div>


<div className="right-column">


  <div className="product-description">
    <span>Flavour: {cakess.flavour}</span>
    <h1>{cakess.name}</h1>
    <p>Rating: {cakess.ratings}/5</p>
    <p>Reviews: {cakess.reviews}/100</p>
    <p>Likes: {cakess.likes}</p>
    <p>{cakess.description}</p>
  </div>


  <div className="product-configuration">


    <div className="product-color">

    </div>


    <div className="cable-config">

      <div className="cable-choose">
        <p>Weight: {cakess.weight}Kg</p>
       {cakess.eggless==true? ( <p>Eggless: Yes</p> ):(<p>Eggless: No </p>)}
        <p>Indegrients: {cakess.ingredients}</p>
        <p>Rs.{cakess.price}</p>
      </div>
    </div>
  </div>


  <div className="product-price">
    <button class="cart-btn" onClick={addTocart}>Add to cart</button>
  </div>
</div>
</main>

    </div>
  );
}

export default CakeDetails;
