import React, { useState,useEffect } from 'react';
import ReactDOM from 'react-dom';
import Footer from './Footer';
import { useHistory } from "react-router-dom";
import axios from 'axios';
import {withRouter} from "react-router-dom"
import { connect } from "react-redux"
import { loginmiddleware } from '../reduxstore/middlewares';

function Login(props){

    const [employee, setemployee] = useState({ email: '', password: ''}); 
    const [loginmes, setloginmes] = useState('');   

    let history = useHistory();

    const Login = (e) => {    
    e.preventDefault();    

    const data = { email:employee.email, password: employee.password };    
       var middlefunction = loginmiddleware(props.data)
			  props.dispatch(middlefunction)

  };    


  const onChange1 = (e) => {    
    e.persist();    

    setemployee({...employee, [e.target.name]: e.target.value});    
  }  

    return(
        <div>
        <div className="container-fluid" style={{ marginTop:"50px"}}>

     <h2> Login Form</h2>

     {loginmes=='Invalid Credentials' ?(
           <div className="alert alert-danger" role="alert">
           {loginmes}
         </div>
        )
      :("")
      }

        <form onSubmit={Login}>

          <div className="form-group">
            <label for="email">Email:</label>
            <input type="email" className="form-control" id="email" placeholder="Enter Email" name="email" value={this.state.email} onChange={this.changeEmail} />
            <div style={{ fontSize: 12, color: "red" }}>  </div>
          </div>
          <div className="form-group">
            <label for="pwd">Password:</label>
            <input type="password" className="form-control" id="password" placeholder="Enter Password" name="password" value={this.state.password} onChange={this.changePassword} />
            <div style={{ fontSize: 12, color: "red" }}>  </div>
          </div>
          <button type="submit" className="btn btn-success">Submit</button>
          {props.isloading && <label className="alert alert-danger">DATA LOADINg.. ruko zara sabar karo thoda</label>}
        </form>

     </div>
     <Footer></Footer>
      </div>

    )
}

Login =connect(function(state,props){
//	alert("props" + JSON.stringify(props))
  if(state.AuthReducer?.isloggedin==true){
      props.history.push("/")
  }else{
	  return {
		  isloading:state.AuthReducer?.isloading
	  }
  }
})(Login) 
// i passed login to withRouter it return me Login with some addional things
// then i exported modified Login
export default withRouter(Login)
