import Cake from './Cake'
import axios from "axios";
import {useEffect, useState} from "react";
import {connect} from "react-redux";
import {withRouter} from "react-router-dom";
import Footer from './Footer';
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import ReactPaginate from "react-paginate";

const Cakelist = (props) => {
    const [cakes, getCakes] = useState([]);
    const [isLoading, setLoading] = useState(true);
    const [sortDown, setSortDown] = useState(true);
    const [offset, setOffset] = useState(0);
    const [perPage] = useState(8);
    const [pageCount, setPageCount] = useState(0);
    const  [slice, setSlice] = useState([]);

    useEffect(() => {
        axios({
            url: process.env.REACT_APP_API_BASE_URL +'/allcakes',
            method: 'get'
        }).then(res => {
            const cakeList = res.data.data
            getCakes(cakeList);
            if( cakes.length<8){
                setSlice(cakes);

              }
          //  setLoading(false);  
            setSlice(cakes.slice(offset*perPage, perPage*(offset+1)));
            setLoading(false)
        }, err => {
            setLoading(false)
        })
    }, [offset])

    useEffect(() => {
        if (localStorage.getItem('token')) {
            axios({
                url: process.env.REACT_APP_API_BASE_URL + '/cakecart',
                method: 'post',
            }).then(res => {
                const cakeList = res.data.data
                props.dispatch({
                    type: "SHOW_CART",
                    payload: {
                        data: cakeList
                    }
                })
            }, err => {
            })
        }
    }, [])


   const onSorting = (e) => {
        let term = 'Title';
        let option = e.target.value;
        if(option=='high') {

        const copy = [...cakes] // create copy of cases array (new array is created each time function is called)
        copy.sort((a, b) => b.price - a.price) // mutate copy array
        setSortDown(!sortDown); // set new value for sortDown
        getCakes(copy); // pass array to setCases


        }
        else {

        const copy = [...cakes] // create copy of cases array (new array is created each time function is called)
        copy.sort((a, b) => a.price - b.price) // mutate copy array
        setSortDown(!sortDown); // set new value for sortDown
        getCakes(copy); // pass array to setCases

        }
    }

    const handlePageClick = (e) => {
        // alert(e.selected)
         const selectedPage = e.selected;
         setOffset(selectedPage + 1);
       };


    return (
        <div>
          <div className="container-fluid" style={{marginTop:'20px'}}>
              <div className="row">
              <div className="col"></div>
              <div className="col">  </div>
              <div className="col"></div>
              <div className="col"></div>
              <div className="col">  
            <select onChange={onSorting} className="form-control">
                  <option value=""> Sort By Price</option>
                  <option value="high">High to Low</option>
                  <option value="low">Low to High</option>
               </select>
               </div>

               </div>
              </div> 
        <div className="row col-md-12" style={{marginTop:'20px'}}>

            {
                cakes.map((each, index) => {
                    return (
                        <Cake data={each} key={index} page="list"/>
                    )
                })
            }

                { isLoading && <div> 
                    <img src="https://i.gifer.com/YCZH.gif" /> 
            </div>
            }
<ReactPaginate
                    previousLabel={"prev"}
                    nextLabel={"next"}
                    breakLabel={"..."}
                    breakClassName={"break-me"}
                    pageCount={ Math.ceil(cakes.length/perPage)}
                    marginPagesDisplayed={2}
                    pageRangeDisplayed={5}
                    onPageChange={handlePageClick}
                    containerClassName={"pagination"}
                    subContainerClassName={"pages pagination"}
                    activeClassName={"active"}/>

            <Footer></Footer>
        </div>
        </div>
    )
}

export default connect() (withRouter(Cakelist))
